mapper.insert:
INSERT INTO tbl_article (
    bno
    , title
    , content
    , writer
    , regdate
    , viewcnt
) VALUES (
    #{bno}
    , #{title}
    , #{content}
    , #{writer}
    , #{regDate}
    , #{viewCnt}
)

mapper.update:
UPDATE tbl_article
SET
    title = #{title}
    , content = #{content}
WHERE
    bno = #{bno}
